int mahun(int atk)
{
	int ma_hu;
	if (atk <= 10)
		ma_hu = atk / 3;
	else if (atk <= 100)
		ma_hu = atk / 4;
	else if (atk <= 150)
		ma_hu = atk / 5;
	else if (atk <= 200)
		ma_hu = atk / 6;
	else if (atk <= 1000)
		ma_hu = 50;
	else
		ma_hu = 0;
	return ma_hu;
}
int noatk(int atk, int def, int mana1, int mana2, int criti)
{
	int dmg;
	if (criti == 0)
	{
		do
		{
			dmg = (int)pow(((pow(((pow(((double)atk * mana1), 1.4)) / (pow(((double)def * mana2), 1.2))), 0.5) * (pow(fabs(sin((double)atk * mana1 - (double)def * mana2)), 0.2) + atk / def)) + mana1), 0.55) + rand() % (6 + mahun(atk)) + rand() % (1 + 9 * (int)round(pow((double)10, (-2 + round(log10(atk))))));
		} while (dmg <= 0);
	}
	else if (criti == 1)
	{
		do
		{
			dmg = (int)round((pow(((pow(((pow(((double)atk * mana1), 1.4)) / (pow(((double)def * mana2), 1.2))), 0.5) * (pow(fabs(sin((double)atk * mana1 - (double)def * mana2)), 0.2) + atk / def)) + mana1), 0.55)) * 1.5) + rand() % (6 + mahun(atk)) + rand() % (1 + 9 * (int)round(pow(10, (-2 + round(log10(atk))))));
		} while (dmg <= 0);
	}
	return dmg;
}
